/*jshint node:true*/
'use strict';

/**
 * Represents a request object passed to method callback.
 *
 * @constructor
 * @param {object} message - message object
 */
function ILXRequest (message) {
    this._id = message.id;
    this._method = message.method;
    this._params = message.params;
    this._msg = message;
}

/**
 * Returns the parameters of the request.
 */
ILXRequest.prototype.params = function () {
    return this._params;
};

module.exports = ILXRequest;
